    using SafeMath for uint256;

    bool private _finalized;

    event CrowdsaleFinalized();

    constructor ()  {
        _finalized = false;
    }

    /**
     * @return true if the crowdsale is finalized, false otherwise.
     */
    function finalized() public view returns (bool) {
        return _finalized;
    }

